(function(){
  function injectIcon(el){
    var svg='<svg viewBox="0 0 24 24" width="24" height="24" fill="currentColor"><circle cx="12" cy="12" r="10"/></svg>';
    el.innerHTML=svg;
  }
  window.lucide={
    createIcons:function(){
      document.querySelectorAll('[data-lucide]').forEach(injectIcon);
    }
  };
})();
